"use strict";var mainHeaderHeight=document.querySelector(".main__header").offsetHeight,headerHeight=document.querySelector(".header").offsetHeight;window.addEventListener("scroll",function(){(window.pageYOffset||document.documentElement.scrollTop)<mainHeaderHeight-4*headerHeight?(document.querySelector(".header").classList.add("header--top"),document.querySelector(".logo").classList.add("logo--top")):(document.querySelector(".header").classList.remove("header--top"),document.querySelector(".logo").classList.remove("logo--top"))});